import axios from 'axios';
axios.defaults.baseURL = "http://localhost:5211";
const apiUrl = "http://localhost:5211"
export const getTasks = () => axios.get("/tasks");
axios.interceptors.response.use(
  response => response, // אם הכל טוב
  error => {
    console.error("Error in API call:", error);
    return Promise.reject(error);
  }
);

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/tasks`)    
    return result.data;
  },

  addTask: async(name)=>{
    console.log('addTask', name)
    //TODO
    return {};
  },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    //TODO
    return {};
  },

  deleteTask:async()=>{
    console.log('deleteTask')
  }
};
export const addTask = (task) => axios.post("http://localhost:5211/tasks", task);
export const updateTask = (id, task) => axios.put(`http://localhost:5211/tasks/${id}`, task);
export const deleteTask = (id) => axios.delete(`http://localhost:5211/tasks/${id}`);
