  },

  deleteTask:async()=>{
    console.log('deleteTask')
  }
};
export const addTask = (task) => axios.post("http://localhost:5211/tasks", task);
export const updateTask = (id, task) => axios.put(`http://localhost:5211/tasks/${id}`, task);
